Mapnik.initialize(); // Erforderlich beim erstmaligen AufrufMapDefinition m = new MapDefinition();// Kartenstil einlesenm.loadMap("src/main/java/de/acando/mapnik/styleFile.xml", false);m.setSrs(Projection.SRS900913_PARAMS); // Art der Projektion setzenm.resize(1024, 768); // Größe der Karte in Pixeln/* Ausschnitt zum Rendern festlegen   Koordinaten beschreiben südwestliche/nordöstliche Ecke*/Box2d bounds = new Box2d(890338.51,7046882.51,1319535.49,7378313.47);m.zoomToBox(bounds);Image image = new Image(1024, 768); // Container für gerenderte KarteRenderer.renderAgg(m, image); // Karte rendernimage.saveToFile("map.png", "png"); // Karte als Bild speichern/* Aufräumen */image.dispose();m.dispose();