StyleFactory sf = CommonFactoryFinder.getStyleFactory();Fill fillWater = sf.createFill(ff.literal(Color.BLUE), ff.literal(0.5));PolygonSymbolizer symWater = sf.createPolygonSymbolizer(null,fillWater, "way");Rule rule = sf.createRule();rule.symbolizers().add(symWater);rule.setFilter(ff.greaterOrEqual(ff.property("way_area"), ff.literal(120000)));Style style = sf.createStyle();FeatureTypeStyle fts = sf.createFeatureTypeStyle(new Rule[] { rule });style.featureTypeStyles().add(fts);Layer layer = new FeatureLayer(featuresWater, style);