MapContent map = new MapContent();map.addLayer(layer);Rectangle imageBounds = new Rectangle(0, 0, 256, 256);BufferedImage image = new BufferedImage(256, 256, BufferedImage.TYPE_INT_RGB);Graphics2D gr = image.createGraphics();gr.setPaint(Color.WHITE);gr.fill(imageBounds);gr.setRenderingHint(RenderingHints.KEY_ANTIALIASING,    RenderingHints.VALIE_ANTIALIAS_ON);GTRenderer renderer = new StreamingRenderer();renderer.setMapContent(map);renderer.paint(gr, imageBounds, layerLand.getBounds());File fileToSave = new File("map.png");ImageIO.write(image, "png", fileToSave);map.dispose();